<div <?php if(empty($_GET['context']) || $_GET['context'] !== 'edit') echo get_block_wrapper_attributes( array('class' => "d-flex", 'id' => "logoLink", ) ); else echo 'data-wp-block-props="true"'; ?>> <a href="<?php echo esc_url( get_home_url() ); ?>"><?php if ( !PG_Blocks_v2::getImageSVG( $args, 'logo_img', false) && PG_Blocks_v2::getImageUrl( $args, 'logo_img', 'full' ) ) : ?><img id="jr_head_logo" src="<?php echo PG_Blocks_v2::getImageUrl( $args, 'logo_img', 'full' ) ?>" class="<?php echo (PG_Blocks_v2::getImageField( $args, 'logo_img', 'id', true) ? ('wp-image-' . PG_Blocks_v2::getImageField( $args, 'logo_img', 'id', true)) : '') ?> skip-lazy" alt="<?php echo PG_Blocks_v2::getImageField( $args, 'logo_img', 'alt', true); ?>" width="500.45" height="81.83"/><?php endif; ?><?php if ( PG_Blocks_v2::getImageSVG( $args, 'logo_img', false) ) : ?><?php echo PG_Blocks_v2::mergeInlineSVGAttributes( PG_Blocks_v2::getImageSVG( $args, 'logo_img' ), array( 'id'=> 'jr_head_logo', 'class'=> 'skip-lazy' ) ) ?><?php endif; ?></a> 
